function get_Color(params) {
    return colors[parseInt(Math.random() * 294)];
}





var canvas = document.getElementById("draw");
var ctx = canvas.getContext("2d");
canvas.width = window.innerWidth;
canvas.height = window.innerHeight;
ctx.textAlign = "center";
ctx.imageSmoothingEnabled= false;


function color_Linear(ctx) {
    var grd = ctx.createLinearGradient(0, 0, canvas.width, 0);
    var color = get_Color();
    var name = Object.keys(color)[0];
    var value = Object.values(color)[0];
    var plus = 1/value.length,start=0;
    for(var i in value){
        grd.addColorStop(start, value[i]);
        start+= plus;
    }
    ctx.fillStyle = grd;
    ctx.fillRect(0, 0, canvas.width, canvas.height);
}



function color_Radial(ctx) {
    var grd = ctx.createRadialGradient(parseInt(Math.random() * canvas.width / 2), parseInt(Math.random() * canvas.width / 2), parseInt(Math.random() * 400),parseInt(Math.random() * 400), parseInt(Math.random() * 400), parseInt(Math.random() * 400));
    var color = get_Color();
    var name = Object.keys(color)[0];
    var value = Object.values(color)[0];
    var plus = 1 / value.length,
        start = 0;
    for (var i in value) {
        grd.addColorStop(start, value[i]);
        start += plus;
    }
    ctx.fillStyle = grd;
    ctx.fillRect(0, 0, canvas.width, canvas.height);
}



function generate(){
	draw(quotes[parseInt(Math.random()*79)]);
}




function draw(text) {
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    var loop = parseInt(Math.random()*20);
    for(var s=0;s<loop;s++){
    color_Radial(ctx);
}
    let splitter = 9;
    var counter = 0,
        data = "";
    let word = text.split(" ");
    let text_width = text.length;
    let font_size = 20;
    let x = canvas.width / 2,
        y = canvas.height / 2;
    y -= parseInt(((word.length / splitter) * font_size));
    for (let i in word) {
        ctx.font = `${font_size}px 'Josefin Slab'`;
        var temp_font = font_size;
        counter += 1;
        data += word[i] + " ";
        if (counter == splitter) {

            while (canvas.width - 200 > ctx.measureText(data).width) {

                ctx.font = `${temp_font}px 'Josefin Slab'`;
                temp_font += 1;
            }
            ctx.fillStyle = "black";
            y += temp_font / 1.2;
            ctx.fillText(data, x, y);
            counter = 0;
            data = "";
        }
        if (i == word.length - 1) {
        	temp_font = font_size;
            while (canvas.width - 1000 > ctx.measureText(data).width && ctx.measureText(data).width > 1) {
                ctx.font = `${temp_font}px 'Josefin Slab'`;
                temp_font += 1;
            }
            ctx.fillText(data, x, y + temp_font);
        }
    }
}






































